function initUpload(dom) {
    FilePond.registerPlugin(FilePondPluginImagePreview);  // Thm plugin preview nh
    FilePond.registerPlugin(FilePondPluginMediaPreview);  // Thm plugin preview audio
    var dom = (dom) ? $(dom) : $(document);
    dom.find('input[type="file"]').each(function(){
        var self = $(this);
        var hiddenField = self.parent().find("input[type=hidden]");
        var _channel = self.attr('data-channel');
        var _type = self.attr('data-type') || 'image';
        var isMultiUpload = (self.attr('multiple')) ? 1 : 0;
        if (!_channel) {
            return false;
        }
        var files = [];
        var val_files = hiddenField.val();
        if (val_files) {
            if (isMultiUpload == 0) {
                val_files = [val_files];
            }
            else {
                val_files = JSON.parse(val_files);
            }
            console.log(val_files);
            $.each(val_files,function(K,item){
                files.push(
                {
                    source: item,
                    options: {type: 'local'}
                });
            });
        }
        self.filepond({
            files: files,
            //allowMultiple: true,
            //name: 'files',
            server: {
                url: '',
                timeout: 7000,
                process: {
                    url: 'https://uploads.ebomb.edu.vn/',
                    method: 'POST',
                    headers: {
                        'Authorization': 'Bearer ' + getCookie('imap_authen_access_token'),
                        'channel' : _channel,
                        'type' : _type
                    },
                    withCredentials: false,
                    onload: (res) => {
                        res = JSON.parse(res)
                        if (res.error) {
                            alert(res.message);
                        } else {
                            if (isMultiUpload) {
                                var currentVal = hiddenField.val();
                                currentVal = (!currentVal) ? [] : JSON.parse(currentVal);
                       
                                currentVal.push(res.path);
                                hiddenField.val(JSON.stringify(currentVal));
                            }
                            else {
                                hiddenField.val(res.path);    
                            }
                           
                            return res.path;
                        }
                    },
                    onerror: (response) => {
                    	alert('Li Upload');
                    }
                    //,
                    //ondata: (formData) => {
                        //formData.append('channel', _channel);
                        //return formData;
                    //},
                },
                restore: null,
                load: 'https://st.ebomb.edu.vn/',
                fetch: null
            },
            onremovefile: (error,file) => {
                if (isMultiUpload) {
                	console.log(file.serverId);
                    var currentVal = hiddenField.val();
                    currentVal = (!currentVal) ? [] : JSON.parse(currentVal);
                    //////////////
                    var index = currentVal.indexOf(file.serverId);
                    if (index > -1) {
                        currentVal.splice(index, 1);
                    }
                    hiddenField.val(JSON.stringify(currentVal));
                   
                }
                else {
                    hiddenField.val("");    
                }
            },
        });
    })
}
